import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { useAuth } from '../services/AuthContext';
import api from '../services/api';

const RentalTracking = () => {
  const { user, updateUser } = useAuth();
  const navigate = useNavigate();
  const [activeRental, setActiveRental] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchActiveRental();
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const fetchActiveRental = async () => {
    try {
      const response = await api.get('/rentals/active');
      setActiveRental(response.data);
    } catch (error) {
      console.log('No active rental');
    } finally {
      setLoading(false);
    }
  };

  const calculateDuration = () => {
    if (!activeRental) return { hours: 0, minutes: 0 };
    const start = new Date(activeRental.startTime);
    const diff = currentTime - start;
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    return { hours, minutes };
  };

  const calculateCurrentCost = () => {
    const { hours } = calculateDuration();
    if (hours === 0) return 7; // Minimum 1 hour charge
    return hours <= 7 ? (hours + 1) * 7 : Math.ceil((hours + 1) / 24) * 70;
  };

  const handlePayment = async () => {
    const amount = calculateCurrentCost();

    const options = {
      key: process.env.REACT_APP_RAZORPAY_KEY_ID || 'rzp_test_key',
      amount: amount * 100,
      currency: 'INR',
      name: 'URS - Umbrella Rental',
      description: `Rental for ${activeRental.umbrella.umbrellaId}`,
      handler: async (response) => {
        try {
          await api.post(`/rentals/${activeRental._id}/pay`, {
            paymentId: response.razorpay_payment_id
          });

          setActiveRental(prev => ({ ...prev, unlocked: true, paymentStatus: 'completed' }));
          alert('Payment successful! Umbrella unlocked.');
        } catch (error) {
          alert('Payment verification failed');
        }
      },
      prefill: {
        email: user.email
      },
      theme: {
        color: '#667eea'
      }
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  const handleEndRental = async () => {
    if (window.confirm('Are you sure you want to end this rental?')) {
      try {
        const response = await api.post(`/rentals/${activeRental._id}/end`);
        const { rental, invoice } = response.data;

        updateUser({ walletBalance: user.walletBalance - rental.totalAmount });

        alert(`Rental ended! Total cost: ‚Çπ${rental.totalAmount}`);
        navigate('/dashboard');
      } catch (error) {
        alert('Failed to end rental');
      }
    }
  };

  if (loading) {
    return (
      <div>
        <Navbar />
        <div className="container">
          <div className="card text-center">
            <h2>Loading...</h2>
          </div>
        </div>
      </div>
    );
  }

  if (!activeRental) {
    return (
      <div>
        <Navbar />
        <div className="container">
          <div className="card text-center">
            <h2 style={{ color: '#6b7280', marginBottom: '16px' }}>No Active Rental</h2>
            <p style={{ color: '#6b7280', marginBottom: '20px' }}>
              You don't have any active umbrella rentals.
            </p>
            <button 
              className="btn btn-primary"
              onClick={() => navigate('/umbrellas')}
            >
              Rent an Umbrella
            </button>
          </div>
        </div>
      </div>
    );
  }

  const { hours, minutes } = calculateDuration();
  const currentCost = calculateCurrentCost();

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="card">
          <h2 style={{ marginBottom: '20px', color: '#1f2937' }}>üìç Rental Tracking</h2>

          <div className="grid grid-2">
            <div className="card" style={{ background: '#f0f9ff', border: '1px solid #0ea5e9' }}>
              <h3 style={{ color: '#0c4a6e', marginBottom: '12px' }}>‚òÇÔ∏è Umbrella Details</h3>
              <div style={{ marginBottom: '8px' }}>
                <strong>ID:</strong> {activeRental.umbrella.umbrellaId}
              </div>
              <div style={{ marginBottom: '8px' }}>
                <strong>Color:</strong> {activeRental.umbrella.color}
              </div>
              <div style={{ marginBottom: '8px' }}>
                <strong>Status:</strong> 
                <span style={{ 
                  color: activeRental.unlocked ? '#10b981' : '#f59e0b',
                  fontWeight: 'bold',
                  marginLeft: '8px'
                }}>
                  {activeRental.unlocked ? 'üîì Unlocked' : 'üîí Locked'}
                </span>
              </div>
            </div>

            <div className="card" style={{ background: '#f0fdf4', border: '1px solid #10b981' }}>
              <h3 style={{ color: '#065f46', marginBottom: '12px' }}>‚è±Ô∏è Duration & Cost</h3>
