import React, { useState, useEffect } from 'react';
import api from '../services/api';

const UserStats = () => {
  const [stats, setStats] = useState({ totalUsers: 0, totalTransactions: 0 });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await api.get('/auth/stats');
        setStats(response.data);
      } catch (error) {
        console.error('Failed to fetch stats');
      }
    };

    fetchStats();
    const interval = setInterval(fetchStats, 5000); // Update every 5 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      position: 'fixed',
      bottom: '20px',
      left: '20px',
      background: 'white',
      border: '1px solid #e5e7eb',
      borderRadius: '8px',
      padding: '12px 16px',
      boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
      zIndex: 1000,
      fontSize: '14px'
    }}>
      <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>Platform Stats</div>
      <div>ðŸ‘¥ Users: {stats.totalUsers}</div>
      <div>ðŸ’³ Transactions: {stats.totalTransactions}</div>
    </div>
  );
};

export default UserStats;
