        </div>
      </div>
    );
  };

  const getTransactionIcon = (type) => {
    switch (type) {
      case 'deposit': return 'üí∞';
      case 'cashback': return 'üéÅ';
      case 'rental': return '‚òÇÔ∏è';
      case 'refund': return '‚Ü©Ô∏è';
      default: return 'üí≥';
    }
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="card">
          <h2 style={{ marginBottom: '20px', color: '#1f2937' }}>üí≥ Your Money Stuff</h2>

          <div className="grid grid-2">
            <div className="card" style={{ background: '#f0f9ff', border: '1px solid #0ea5e9' }}>
              <h3 style={{ color: '#0c4a6e', marginBottom: '12px' }}>What you've got</h3>
              <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#0c4a6e' }}>
                ‚Çπ{user?.walletBalance || 0}
              </div>
              {!user?.depositMade && (
                <div style={{ 
                  background: '#dcfce7', 
                  color: '#166534', 
                  padding: '8px 12px', 
                  borderRadius: '6px', 
                  marginTop: '12px',
                  fontSize: '0.875rem'
                }}>
                  üéÅ First time? We'll add ‚Çπ100 bonus on ‚Çπ300+ deposit!
                </div>
