            <div className="card">
              <h3 style={{ marginBottom: '16px', color: '#1f2937' }}>Top up your wallet</h3>
              <input
                type="number"
                placeholder="Enter amount"
                className="input"
                value={amount}
                onChange={(e) => setAmount(Number(e.target.value))}
                min="100"
              />
              <div style={{ display: 'flex', gap: '8px', marginBottom: '16px' }}>
                {[300, 500, 1000, 2000].map((preset) => (
                  <button
                    key={preset}
                    onClick={() => setAmount(preset)}
                    style={{
                      padding: '8px 12px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      background: amount === preset ? '#667eea' : 'white',
                      color: amount === preset ? 'white' : '#374151',
                      cursor: 'pointer'
                    }}
                  >
                    ‚Çπ{preset}
                  </button>
                ))}
              </div>
              <button 
                className="btn btn-primary" 
                style={{ width: '100%' }}
                onClick={handleDeposit}
                disabled={loading}
              >
                {loading ? 'Adding money...' : `Add ‚Çπ${amount}`}
              </button>
            </div>
          </div>

          <div className="card" style={{ marginTop: '20px' }}>
            <h3 style={{ marginBottom: '16px', color: '#1f2937' }}>üìä Your Money Moves</h3>
            {transactions.length === 0 ? (
              <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
                Nothing here yet! Start by adding some money üöÄ
              </p>
            ) : (
              <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                {transactions.map((transaction) => (
                  <div key={transaction._id} style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '12px',
                    border: '1px solid #e5e7eb',
                    borderRadius: '8px',
                    marginBottom: '8px'
                  }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                      <span style={{ fontSize: '1.5rem' }}>
                        {getTransactionIcon(transaction.type)}
                      </span>
                      <div>
                        <div style={{ fontWeight: '600', textTransform: 'capitalize' }}>
                          {transaction.type}
                        </div>
                        <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>
