import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../services/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
        <h2 style={{ color: '#667eea' }}>☂️ URS</h2>
        <div className="wallet-balance">
          ₹{user?.walletBalance || 0}
        </div>
      </div>
      <div style={{ display: 'flex', gap: '16px' }}>
        <button className="btn btn-primary" onClick={() => navigate('/dashboard')}>
          Dashboard
        </button>
        <button className="btn btn-primary" onClick={() => navigate('/umbrellas')}>
          Rent Umbrella
        </button>
        <button className="btn btn-primary" onClick={() => navigate('/wallet')}>
          Wallet
        </button>
        <button className="btn" onClick={handleLogout} style={{ background: '#ef4444', color: 'white' }}>
          Logout
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
