import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { useAuth } from '../services/AuthContext';
import api from '../services/api';

const RentalTracking = () => {
  const { user, updateUser } = useAuth();
  const navigate = useNavigate();
  const [activeRental, setActiveRental] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchActiveRental();
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    if (activeRental && window.google) {
      initMap();
    }
  }, [activeRental]);

  const initMap = () => {
    const { latitude, longitude } = activeRental.umbrella.location;

    const map = new window.google.maps.Map(document.getElementById('map'), {
      zoom: 18,
      center: { lat: parseFloat(latitude), lng: parseFloat(longitude) },
      mapTypeId: 'hybrid'
    });

    new window.google.maps.Marker({
      position: { lat: parseFloat(latitude), lng: parseFloat(longitude) },
      map: map,
      title: `${activeRental.umbrella.umbrellaId} - ${activeRental.umbrella.location.address}`,
      icon: {
        url: 'data:image/svg+xml;charset=UTF-8,' + encodeURIComponent(`
          <svg width="40" height="40" viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg">
            <circle cx="20" cy="20" r="18" fill="#3b82f6" stroke="white" stroke-width="2"/>
            <text x="20" y="26" text-anchor="middle" fill="white" font-size="20">‚òÇ</text>
          </svg>
        `),
        scaledSize: new window.google.maps.Size(40, 40)
      }
    });

    // Add campus boundary
    const campusBounds = new window.google.maps.Rectangle({
      bounds: {
        north: 30.7600,
        south: 30.7575,
        east: 76.5690,
        west: 76.5660
      },
      strokeColor: '#10b981',
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: '#10b981',
      fillOpacity: 0.1,
      map: map
    });
  };

  const fetchActiveRental = async () => {
    try {
      const response = await api.get('/rentals/active');
      setActiveRental(response.data);
    } catch (error) {
      console.log('No active rental');
    } finally {
      setLoading(false);
    }
  };

  const calculateDuration = () => {
    if (!activeRental) return { hours: 0, minutes: 0 };
    const start = new Date(activeRental.startTime);
    const diff = currentTime - start;
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    return { hours, minutes };
  };

  const calculateCurrentCost = () => {
    const { hours } = calculateDuration();
    if (hours === 0) return 7; // Minimum 1 hour charge
    return hours <= 7 ? (hours + 1) * 7 : Math.ceil((hours + 1) / 24) * 70;
  };

  const [showPaymentModal, setShowPaymentModal] = useState(false);

  const handlePayment = () => {
    setShowPaymentModal(true);
  };

  const processRentalPayment = async (method) => {
    try {
      await api.post(`/rentals/${activeRental._id}/pay`, {
        paymentId: `${method}_${Date.now()}`,
        paymentMethod: method
      });

      const response = await api.post(`/rentals/${activeRental._id}/pay`, {
        paymentId: `${method}_${Date.now()}`,
        paymentMethod: method
      });

      setActiveRental(prev => ({ ...prev, unlocked: true, paymentStatus: 'completed' }));
      updateUser({ walletBalance: response.data.walletBalance });
      alert(`Payment successful via ${method}! ‚Çπ${response.data.amountDeducted} deducted. Umbrella unlocked.`);
      setShowPaymentModal(false);
    } catch (error) {
      alert('Payment verification failed');
    }
  };

  const PaymentModal = () => {
    if (!showPaymentModal) return null;
    const amount = calculateCurrentCost();

    return (
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'rgba(0,0,0,0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1000
      }}>
        <div className="card" style={{ width: '400px', maxWidth: '90vw' }}>
          <h3 style={{ marginBottom: '20px' }}>Pay for Rental</h3>
          <div style={{ marginBottom: '16px', fontSize: '1.2rem', fontWeight: 'bold' }}>
            Amount: ‚Çπ{amount}
          </div>

          <div style={{ display: 'grid', gap: '12px' }}>
            <button
              className="btn"
              style={{ background: '#10b981', color: 'white', padding: '16px', textAlign: 'left' }}
              onClick={() => processRentalPayment('UPI')}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <span style={{ fontSize: '1.5rem' }}>üì±</span>
                <div>
                  <div style={{ fontWeight: 'bold' }}>UPI Payment</div>
                  <div style={{ fontSize: '0.875rem', opacity: 0.8 }}>Pay using UPI ID</div>
                </div>
              </div>
            </button>

            <button
              className="btn"
              style={{ background: '#3b82f6', color: 'white', padding: '16px', textAlign: 'left' }}
              onClick={() => processRentalPayment('QR Code')}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <span style={{ fontSize: '1.5rem' }}>üì∑</span>
                <div>
                  <div style={{ fontWeight: 'bold' }}>QR Code</div>
                  <div style={{ fontSize: '0.875rem', opacity: 0.8 }}>Scan QR to pay</div>
                </div>
              </div>
            </button>

            <button
              className="btn"
              style={{ background: '#8b5cf6', color: 'white', padding: '16px', textAlign: 'left' }}
              onClick={() => processRentalPayment('Card')}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <span style={{ fontSize: '1.5rem' }}>üí≥</span>
                <div>
                  <div style={{ fontWeight: 'bold' }}>Credit/Debit Card</div>
                  <div style={{ fontSize: '0.875rem', opacity: 0.8 }}>Visa, Mastercard, RuPay</div>
                </div>
              </div>
            </button>

            <button
              className="btn"
              style={{ background: '#f59e0b', color: 'white', padding: '16px', textAlign: 'left' }}
              onClick={() => processRentalPayment('Wallet')}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                <span style={{ fontSize: '1.5rem' }}>üëõ</span>
                <div>
                  <div style={{ fontWeight: 'bold' }}>Digital Wallet</div>
                  <div style={{ fontSize: '0.875rem', opacity: 0.8 }}>Paytm, PhonePe, GPay</div>
                </div>
              </div>
            </button>
          </div>

          <button
            className="btn"
            style={{ background: '#6b7280', color: 'white', width: '100%', marginTop: '16px' }}
            onClick={() => setShowPaymentModal(false)}
          >
            Cancel
          </button>
        </div>
      </div>
    );
  };

  const handleEndRental = async () => {
    if (window.confirm('Are you sure you want to end this rental?')) {
      try {
        const response = await api.post(`/rentals/${activeRental._id}/end`);
        const { rental, invoice } = response.data;

        updateUser({ walletBalance: user.walletBalance - rental.totalAmount });

        alert(`Rental ended! Total cost: ‚Çπ${rental.totalAmount}`);
        navigate('/dashboard');
      } catch (error) {
        alert('Failed to end rental');
      }
    }
  };

  // Load Google Maps if not already loaded
  useEffect(() => {
    if (!window.google && !document.querySelector('script[src*="maps.googleapis.com"]')) {
      const script = document.createElement('script');
      script.src = 'https://maps.googleapis.com/maps/api/js?key=AIzaSyBFw0Qbyq9zTbyTHiHW-_-_-_-_-_-_-_&libraries=places';
      script.async = true;
      document.head.appendChild(script);
    }
  }, []);

  if (loading) {
    return (
      <div>
        <Navbar />
        <div className="container">
          <div className="card text-center">
            <h2>Loading...</h2>
          </div>
        </div>
      </div>
    );
  }

  if (!activeRental) {
    return (
      <div>
        <Navbar />
        <div className="container">
          <div className="card text-center">
            <h2 style={{ color: '#6b7280', marginBottom: '16px' }}>No Active Rental</h2>
            <p style={{ color: '#6b7280', marginBottom: '20px' }}>
              You don't have any active umbrella rentals.
            </p>
            <button 
              className="btn btn-primary"
              onClick={() => navigate('/umbrellas')}
            >
              Rent an Umbrella
            </button>
          </div>
        </div>
      </div>
    );
  }

  const { hours, minutes } = calculateDuration();
  const currentCost = calculateCurrentCost();

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="card">
          <h2 style={{ marginBottom: '20px', color: '#1f2937' }}>üìç Rental Tracking</h2>

          <div className="grid grid-2">
            <div className="card" style={{ background: '#f0f9ff', border: '1px solid #0ea5e9' }}>
              <h3 style={{ color: '#0c4a6e', marginBottom: '12px' }}>‚òÇÔ∏è Umbrella Details</h3>
              <div style={{ marginBottom: '8px' }}>
                <strong>ID:</strong> {activeRental.umbrella.umbrellaId}
              </div>
              <div style={{ marginBottom: '8px' }}>
                <strong>Color:</strong> {activeRental.umbrella.color}
              </div>
              <div style={{ marginBottom: '8px' }}>
                <strong>Status:</strong> 
                <span style={{ 
                  color: activeRental.unlocked ? '#10b981' : '#f59e0b',
                  fontWeight: 'bold',
                  marginLeft: '8px'
                }}>
                  {activeRental.unlocked ? 'üîì Unlocked' : 'üîí Locked'}
                </span>
              </div>
            </div>

            <div className="card" style={{ background: '#f0fdf4', border: '1px solid #10b981' }}>
              <h3 style={{ color: '#065f46', marginBottom: '12px' }}>‚è±Ô∏è Duration & Cost</h3>
              <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#065f46', marginBottom: '8px' }}>
                {hours}h {minutes}m
              </div>
              <div style={{ marginBottom: '8px' }}>
                <strong>Started:</strong> {new Date(activeRental.startTime).toLocaleString()}
              </div>
              <div style={{ fontSize: '1.2rem', fontWeight: 'bold', color: '#065f46' }}>
                Current Cost: ‚Çπ{currentCost}
              </div>
            </div>
          </div>

          {!activeRental.unlocked && (
            <div className="card" style={{ background: '#fef3c7', border: '1px solid #f59e0b', marginTop: '20px' }}>
              <h3 style={{ color: '#92400e', marginBottom: '12px' }}>üí≥ Payment Required</h3>
              <p style={{ color: '#92400e', marginBottom: '16px' }}>
                Please complete payment to unlock your umbrella.
              </p>
              <button 
                className="btn btn-primary"
                onClick={handlePayment}
              >
                Pay ‚Çπ{currentCost} & Unlock
              </button>
            </div>
          )}

          <div className="card" style={{ marginTop: '20px' }}>
            <h3 style={{ marginBottom: '16px', color: '#1f2937' }}>üó∫Ô∏è Live Location</h3>
            <div style={{ marginBottom: '12px', padding: '8px 12px', background: '#f0f9ff', borderRadius: '6px' }}>
              <strong>üìç Current Location:</strong> {activeRental.umbrella.location?.address || 'Chandigarh University Campus'}
            </div>
            <div style={{ marginBottom: '12px', padding: '8px 12px', background: '#f0fdf4', borderRadius: '6px' }}>
              <strong>üåê GPS Coordinates:</strong> {activeRental.umbrella.location?.latitude}, {activeRental.umbrella.location?.longitude}
            </div>
            <div 
              id="map" 
              style={{ 
                height: '300px', 
                borderRadius: '8px',
                border: '1px solid #e5e7eb'
              }}
            ></div>
          </div>

          <div className="card" style={{ marginTop: '20px' }}>
            <h3 style={{ marginBottom: '16px', color: '#1f2937' }}>üéØ Actions</h3>
            <div style={{ display: 'flex', gap: '12px', flexWrap: 'wrap' }}>
              {!activeRental.unlocked && (
                <button 
                  className="btn btn-primary"
                  onClick={handlePayment}
                >
                  üí≥ Pay & Unlock
                </button>
              )}

              {activeRental.unlocked && (
                <button 
                  className="btn"
                  style={{ background: '#ef4444', color: 'white' }}
                  onClick={handleEndRental}
                >
                  üèÅ End Rental
                </button>
              )}

              <button 
                className="btn"
                style={{ background: '#6b7280', color: 'white' }}
                onClick={() => navigate('/dashboard')}
              >
                üè† Back to Dashboard
              </button>
            </div>
          </div>

          <div className="card" style={{ background: '#f8fafc', marginTop: '20px' }}>
            <h4 style={{ marginBottom: '12px', color: '#374151' }}>üí° Pricing Information</h4>
            <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>
              ‚Ä¢ ‚Çπ7 per hour for up to 7 hours<br/>
              ‚Ä¢ ‚Çπ70 per day after 7 hours<br/>
              ‚Ä¢ Timer calculates rent in real-time<br/>
              ‚Ä¢ Payment unlocks the umbrella instantly
            </div>
          </div>
        </div>

        <PaymentModal />
      </div>
    </div>
  );
};

export default RentalTracking;
