import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import { useAuth } from '../services/AuthContext';
import api from '../services/api';

const Wallet = () => {
  const { user, updateUser } = useAuth();
  const [amount, setAmount] = useState(300);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const response = await api.get('/wallet/transactions');
      setTransactions(response.data);
    } catch (error) {
      console.error('Failed to fetch transactions');
    }
  };

  const handleDeposit = async () => {
    if (amount < 100) {
      alert('Minimum deposit amount is ‚Çπ100');
      return;
    }

    setLoading(true);
    try {
      const response = await api.post('/wallet/deposit', { amount });
      const { orderId } = response.data;

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID || 'rzp_test_key',
        amount: amount * 100,
        currency: 'INR',
        name: 'URS - Umbrella Rental',
        description: 'Wallet Deposit',
        order_id: orderId,
        handler: async (response) => {
          try {
            const verifyResponse = await api.post('/wallet/verify-deposit', {
              paymentId: response.razorpay_payment_id,
              amount
            });

            updateUser({ walletBalance: verifyResponse.data.walletBalance });
            alert('Deposit successful!');
            fetchTransactions();
            setAmount(300);
          } catch (error) {
            alert('Payment verification failed');
          }
        },
        prefill: {
          email: user.email
        },
        theme: {
          color: '#667eea'
        }
      };

      const razorpay = new window.Razorpay(options);
      razorpay.open();
    } catch (error) {
      alert('Failed to initiate payment');
    } finally {
      setLoading(false);
    }
  };

  const getTransactionIcon = (type) => {
    switch (type) {
      case 'deposit': return 'üí∞';
      case 'cashback': return 'üéÅ';
      case 'rental': return '‚òÇÔ∏è';
      case 'refund': return '‚Ü©Ô∏è';
      default: return 'üí≥';
    }
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="card">
          <h2 style={{ marginBottom: '20px', color: '#1f2937' }}>üí≥ Wallet Management</h2>

          <div className="grid grid-2">
            <div className="card" style={{ background: '#f0f9ff', border: '1px solid #0ea5e9' }}>
              <h3 style={{ color: '#0c4a6e', marginBottom: '12px' }}>Current Balance</h3>
              <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#0c4a6e' }}>
                ‚Çπ{user?.walletBalance || 0}
              </div>
              {!user?.depositMade && (
                <div style={{ 
                  background: '#dcfce7', 
                  color: '#166534', 
                  padding: '8px 12px', 
                  borderRadius: '6px', 
                  marginTop: '12px',
                  fontSize: '0.875rem'
                }}>
                  üéÅ Get ‚Çπ100 cashback on first deposit of ‚Çπ300+
                </div>
              )}
            </div>

            <div className="card">
              <h3 style={{ marginBottom: '16px', color: '#1f2937' }}>Add Money</h3>
              <input
                type="number"
                placeholder="Enter amount"
                className="input"
                value={amount}
                onChange={(e) => setAmount(Number(e.target.value))}
                min="100"
              />
              <div style={{ display: 'flex', gap: '8px', marginBottom: '16px' }}>
                {[300, 500, 1000, 2000].map((preset) => (
                  <button
                    key={preset}
                    onClick={() => setAmount(preset)}
                    style={{
                      padding: '8px 12px',
                      border: '1px solid #d1d5db',
                      borderRadius: '6px',
                      background: amount === preset ? '#667eea' : 'white',
                      color: amount === preset ? 'white' : '#374151',
                      cursor: 'pointer'
                    }}
                  >
                    ‚Çπ{preset}
                  </button>
                ))}
              </div>
              <button 
                className="btn btn-primary" 
                style={{ width: '100%' }}
                onClick={handleDeposit}
                disabled={loading}
              >
                {loading ? 'Processing...' : `Add ‚Çπ${amount}`}
              </button>
            </div>
          </div>

          <div className="card" style={{ marginTop: '20px' }}>
            <h3 style={{ marginBottom: '16px', color: '#1f2937' }}>üìä Transaction History</h3>
            {transactions.length === 0 ? (
              <p style={{ color: '#6b7280', textAlign: 'center', padding: '20px' }}>
                No transactions yet
              </p>
            ) : (
              <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                {transactions.map((transaction) => (
                  <div key={transaction._id} style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '12px',
                    border: '1px solid #e5e7eb',
                    borderRadius: '8px',
                    marginBottom: '8px'
                  }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                      <span style={{ fontSize: '1.5rem' }}>
                        {getTransactionIcon(transaction.type)}
                      </span>
                      <div>
                        <div style={{ fontWeight: '600', textTransform: 'capitalize' }}>
                          {transaction.type}
                        </div>
                        <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>
                          {new Date(transaction.createdAt).toLocaleString()}
                        </div>
                        {transaction.description && (
                          <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>
                            {transaction.description}
                          </div>
                        )}
                      </div>
                    </div>
                    <div style={{ 
                      fontWeight: 'bold', 
                      color: transaction.amount > 0 ? '#10b981' : '#ef4444' 
                    }}>
                      {transaction.amount > 0 ? '+' : ''}‚Çπ{Math.abs(transaction.amount)}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Wallet;
