import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { useAuth } from '../services/AuthContext';
import api from '../services/api';

const UmbrellaSelection = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [umbrellas, setUmbrellas] = useState([]);
  const [filteredUmbrellas, setFilteredUmbrellas] = useState([]);
  const [selectedColor, setSelectedColor] = useState('');
  const [loading, setLoading] = useState(true);

  const colors = ['red', 'blue', 'yellow', 'black', 'green'];
  const colorEmojis = {
    red: 'üî¥',
    blue: 'üîµ', 
    yellow: 'üü°',
    black: '‚ö´',
    green: 'üü¢'
  };

  useEffect(() => {
    fetchUmbrellas();
  }, []);

  useEffect(() => {
    filterUmbrellas();
  }, [umbrellas, selectedColor]);

  const fetchUmbrellas = async () => {
    try {
      const response = await api.get('/umbrellas');
      setUmbrellas(response.data);
    } catch (error) {
      console.error('Failed to fetch umbrellas');
    } finally {
      setLoading(false);
    }
  };

  const filterUmbrellas = () => {
    if (!selectedColor) {
      setFilteredUmbrellas(umbrellas.filter(u => u.isAvailable));
    } else {
      setFilteredUmbrellas(umbrellas.filter(u => u.isAvailable && u.color === selectedColor));
    }
  };

  const handleRentUmbrella = async (umbrellaId) => {
    if (!user?.depositMade) {
      alert('Please make a deposit first');
      navigate('/wallet');
      return;
    }

    try {
      const response = await api.post('/rentals/start', { umbrellaId });
      alert('Rental started! Proceed to payment.');
      navigate('/tracking');
    } catch (error) {
      alert(error.response?.data?.message || 'Failed to start rental');
    }
  };

  if (loading) {
    return (
      <div>
        <Navbar />
        <div className="container">
          <div className="card text-center">
            <h2>Loading umbrellas...</h2>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="card">
          <h2 style={{ marginBottom: '20px', color: '#1f2937' }}>‚òÇÔ∏è Select an Umbrella</h2>

          {!user?.depositMade && (
            <div style={{ 
              background: '#fef3c7', 
              border: '1px solid #f59e0b', 
              borderRadius: '8px', 
              padding: '16px', 
              marginBottom: '20px' 
            }}>
              <p style={{ color: '#92400e', marginBottom: '12px' }}>
                ‚ö†Ô∏è Please make a deposit of ‚Çπ300 before renting an umbrella.
              </p>
              <button 
                className="btn btn-primary"
                onClick={() => navigate('/wallet')}
              >
                Make Deposit
              </button>
            </div>
          )}

          <div style={{ marginBottom: '20px' }}>
            <h3 style={{ marginBottom: '12px', color: '#374151' }}>Filter by Color:</h3>
            <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
              <button
                onClick={() => setSelectedColor('')}
                style={{
                  padding: '8px 16px',
                  border: '1px solid #d1d5db',
                  borderRadius: '20px',
                  background: !selectedColor ? '#667eea' : 'white',
                  color: !selectedColor ? 'white' : '#374151',
                  cursor: 'pointer'
                }}
              >
                All Colors
              </button>
              {colors.map((color) => (
                <button
                  key={color}
                  onClick={() => setSelectedColor(color)}
                  style={{
                    padding: '8px 16px',
                    border: '1px solid #d1d5db',
                    borderRadius: '20px',
                    background: selectedColor === color ? '#667eea' : 'white',
                    color: selectedColor === color ? 'white' : '#374151',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '4px'
                  }}
                >
                  {colorEmojis[color]} {color.charAt(0).toUpperCase() + color.slice(1)}
                </button>
              ))}
            </div>
          </div>

          {filteredUmbrellas.length === 0 ? (
            <div className="card text-center" style={{ background: '#f9fafb' }}>
              <h3 style={{ color: '#6b7280', marginBottom: '8px' }}>No umbrellas available</h3>
              <p style={{ color: '#6b7280' }}>
                {selectedColor ? `No ${selectedColor} umbrellas available right now.` : 'All umbrellas are currently rented.'}
              </p>
            </div>
          ) : (
            <div className="grid grid-2">
              {filteredUmbrellas.map((umbrella) => (
                <div key={umbrella._id} className="card" style={{ 
                  border: umbrella.isAvailable ? '2px solid #10b981' : '2px solid #e5e7eb',
                  transition: 'all 0.3s ease',
                  cursor: 'pointer',
                  position: 'relative',
                  overflow: 'hidden'
                }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '16px' }}>
                    <div>
                      <h3 style={{ color: '#1f2937', marginBottom: '4px' }}>
                        {umbrella.umbrellaId}
                      </h3>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                        <span style={{ fontSize: '1.2rem' }}>{colorEmojis[umbrella.color]}</span>
                        <span style={{ color: '#6b7280', textTransform: 'capitalize' }}>
                          {umbrella.color}
                        </span>
                      </div>
                    </div>
                    <div className={umbrella.isAvailable ? 'status-badge status-available' : 'status-badge status-rented'}>
                      {umbrella.isAvailable ? '‚úÖ Available' : '‚ùå Rented'}
                    </div>
                  </div>

                  <div style={{ marginBottom: '16px' }}>
                    <div style={{ fontSize: '0.875rem', color: '#6b7280', marginBottom: '4px' }}>
                      üìç Location
                    </div>
                    <div style={{ fontSize: '0.875rem', color: '#374151' }}>
                      {umbrella.location?.address || 'Chandigarh University'}
                    </div>
                  </div>

                  <div style={{ 
                    background: '#f8fafc', 
                    padding: '12px', 
                    borderRadius: '8px', 
                    marginBottom: '16px' 
                  }}>
                    <div style={{ fontSize: '0.875rem', color: '#6b7280', marginBottom: '4px' }}>
                      üí∞ Pricing
                    </div>
                    <div style={{ fontSize: '0.875rem', color: '#374151' }}>
                      ‚Çπ7/hour (up to 7 hours) ‚Ä¢ ‚Çπ70/day (after 7 hours)
                    </div>
                  </div>

                  <button
                    className="btn btn-primary"
                    style={{ width: '100%' }}
                    onClick={() => handleRentUmbrella(umbrella._id)}
                    disabled={!umbrella.isAvailable || !user?.depositMade}
                  >
                    {!user?.depositMade ? 'Deposit Required' : 
                     !umbrella.isAvailable ? 'Not Available' : 'Rent This Umbrella'}
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UmbrellaSelection;
